import React, { useEffect, useState } from "react";
import "./ManageNotices.css";

const API =
  process.env.REACT_APP_API_URL ||
  "https://backend-9i6n.onrender.com";

const ManageNotices = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [isImportant, setIsImportant] = useState(false);
  const [file, setFile] = useState(null);

  const [notices, setNotices] = useState([]);

  const [editPopup, setEditPopup] = useState(false);
  const [currentEdit, setCurrentEdit] = useState(null);
  const [editTitle, setEditTitle] = useState("");
  const [editDescription, setEditDescription] = useState("");
  const [editFile, setEditFile] = useState(null);

  const [message, setMessage] = useState("");

  const token = localStorage.getItem("token");

  /* ================= FETCH ================= */
  const fetchNotices = async () => {
    try {
      const res = await fetch(`${API}/api/notices`);
      const data = await res.json();
      setNotices(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("Fetch notice error:", err);
    }
  };

  useEffect(() => {
    fetchNotices();
  }, []);

  /* ================= ADD ================= */
  const addNotice = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("isImportant", isImportant);
    if (file) formData.append("attachment", file);

    await fetch(`${API}/api/notices`, {
      method: "POST",
      headers: {
        Authorization: `Bearer ${token}`,
      },
      body: formData,
    });

    setTitle("");
    setDescription("");
    setIsImportant(false);
    setFile(null);

    setMessage("Notice added successfully ✅");
    setTimeout(() => setMessage(""), 3000);
    fetchNotices();
  };

  /* ================= DELETE ================= */
  const deleteNotice = async (id) => {
    if (!window.confirm("Delete this notice?")) return;

    await fetch(`${API}/api/notices/${id}`, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    setMessage("Notice deleted successfully");
    setTimeout(() => setMessage(""), 3000);
    fetchNotices();
  };

  /* ================= OPEN EDIT ================= */
  const openEditPopup = (notice) => {
    setCurrentEdit(notice);
    setEditTitle(notice.title);
    setEditDescription(notice.description);
    setEditFile(null);
    setEditPopup(true);
  };

  /* ================= UPDATE ================= */
  const updateNotice = async () => {
    const formData = new FormData();
    formData.append("title", editTitle);
    formData.append("description", editDescription);
    if (editFile) formData.append("attachment", editFile);

    await fetch(`${API}/api/notices/${currentEdit._id}`, {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${token}`,
      },
      body: formData,
    });

    setEditPopup(false);
    setCurrentEdit(null);

    setMessage("Notice updated successfully");
    setTimeout(() => setMessage(""), 2000);
    fetchNotices();
  };

  /* ================= ATTACHMENT URL ================= */
  const getAttachmentUrl = (attachment) => {
    if (!attachment) return null;
    return attachment.startsWith("http")
      ? attachment
      : `${API}${attachment}`;
  };

  return (
    <div className="manage-notices">
      <h2>Manage Notices</h2>

      {message && <div className="success-msg">{message}</div>}

      {/* ADD FORM */}
      <form className="notice-form" onSubmit={addNotice}>
        <input
          placeholder="Notice Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        <textarea
          placeholder="Notice Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />

        <label>
          <input
            type="checkbox"
            checked={isImportant}
            onChange={(e) => setIsImportant(e.target.checked)}
          />
          Mark as Important
        </label>

        <input
          type="file"
          accept=".pdf,.jpg,.jpeg,.png"
          onChange={(e) => setFile(e.target.files[0])}
        />

        <button type="submit">Add Notice</button>
      </form>

      {/* NOTICE LIST */}
      <div className="notice-list">
        {notices.map((n) => (
          <div
            key={n._id}
            className={`notice-card ${n.isImportant ? "important" : ""}`}
          >
            <h4>{n.title}</h4>
            <p>{n.description}</p>

            {n.attachment && (
              <a
                href={getAttachmentUrl(n.attachment)}
                target="_blank"
                rel="noopener noreferrer"
              >
                View Attachment
              </a>
            )}

            <div className="card-actions">
              <button onClick={() => openEditPopup(n)}>Edit</button>
              <button onClick={() => deleteNotice(n._id)}>Delete</button>
            </div>
          </div>
        ))}
      </div>

      {/* EDIT MODAL */}
      {editPopup && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Edit Notice</h3>

            <input
              value={editTitle}
              onChange={(e) => setEditTitle(e.target.value)}
            />

            <textarea
              value={editDescription}
              onChange={(e) => setEditDescription(e.target.value)}
            />

            {currentEdit?.attachment && (
              <a
                href={getAttachmentUrl(currentEdit.attachment)}
                target="_blank"
                rel="noopener noreferrer"
              >
                View Current Attachment
              </a>
            )}

            <input
              type="file"
              accept=".pdf,.jpg,.jpeg,.png"
              onChange={(e) => setEditFile(e.target.files[0])}
            />

            <button onClick={updateNotice}>Save</button>
            <button onClick={() => setEditPopup(false)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageNotices;
