import React, { useEffect, useState } from "react";
import "./ManageNotices.css";

const API =
  process.env.REACT_APP_API_URL ||
  "https://backend-9i6n.onrender.com";

const ManageNotices = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [isImportant, setIsImportant] = useState(false);
  const [noticeType, setNoticeType] = useState("notice"); // ‚úÖ NEW
  const [file, setFile] = useState(null);

  const [notices, setNotices] = useState([]);

  const [editPopup, setEditPopup] = useState(false);
  const [currentEdit, setCurrentEdit] = useState(null);
  const [editTitle, setEditTitle] = useState("");
  const [editDescription, setEditDescription] = useState("");
  const [editFile, setEditFile] = useState(null);

  const [isAdding, setIsAdding] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [isDeleting, setIsDeleting] = useState(null);

  const [popup, setPopup] = useState({ show: false, message: "", type: "" });

  const token = localStorage.getItem("token");

  /* ================= POPUP ================= */
  const showPopup = (message, type = "success") => {
    setPopup({ show: true, message, type });
    setTimeout(() => setPopup({ show: false, message: "", type: "" }), 1500);
  };

  /* ================= FETCH ================= */
  const fetchNotices = async () => {
    const res = await fetch(`${API}/api/notices`);
    const data = await res.json();
    setNotices(Array.isArray(data) ? data : []);
  };

  useEffect(() => {
    fetchNotices();
  }, []);

  /* ================= ADD ================= */
  const addNotice = async (e) => {
    e.preventDefault();

    if (noticeType === "list" && !file) {
      showPopup("‡§Ø‡§æ‡§¶‡•Ä‡§∏‡§æ‡§†‡•Ä ‡§´‡§æ‡§à‡§≤ ‡§Ü‡§µ‡§∂‡•ç‡§Ø‡§ï ‡§Ü‡§π‡•á", "error");
      return;
    }

    setIsAdding(true);
    try {
      const formData = new FormData();
      formData.append("title", title);
      formData.append("description", description);
      formData.append("isImportant", isImportant);
      formData.append("noticeType", noticeType);
      if (file) formData.append("attachment", file);

      const res = await fetch(`${API}/api/notices`, {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` },
        body: formData,
      });

      if (res.ok) {
        setTitle("");
        setDescription("");
        setIsImportant(false);
        setNoticeType("notice");
        setFile(null);

        showPopup("üéâ Notice added successfully!");
        fetchNotices();
      } else {
        showPopup("Failed to add notice", "error");
      }
    } catch {
      showPopup("Error while adding notice", "error");
    } finally {
      setIsAdding(false);
    }
  };

  /* ================= DELETE ================= */
  const deleteNotice = async (id) => {
    if (!window.confirm("Delete this notice?")) return;
    setIsDeleting(id);

    await fetch(`${API}/api/notices/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });

    fetchNotices();
    setIsDeleting(null);
    showPopup("üóëÔ∏è Deleted successfully");
  };

  /* ================= EDIT ================= */
  const openEditPopup = (n) => {
    setCurrentEdit(n);
    setEditTitle(n.title);
    setEditDescription(n.description);
    setEditPopup(true);
  };

  const updateNotice = async () => {
    setIsUpdating(true);

    const formData = new FormData();
    formData.append("title", editTitle);
    formData.append("description", editDescription);
    if (editFile) formData.append("attachment", editFile);

    await fetch(`${API}/api/notices/${currentEdit._id}`, {
      method: "PUT",
      headers: { Authorization: `Bearer ${token}` },
      body: formData,
    });

    setEditPopup(false);
    setIsUpdating(false);
    fetchNotices();
    showPopup("‚ú® Updated successfully");
  };

  const getAttachmentUrl = (a) => (a?.startsWith("http") ? a : `${API}${a}`);

  return (
    <div className="manage-notices">
      {/* POPUP */}
      {popup.show && (
        <div className={`popup ${popup.type}`}>
          <p>{popup.message}</p>
        </div>
      )}

      {/* ADD FORM */}
      <form className="notice-form" onSubmit={addNotice}>
        <h3>‚ûï Add Notice / List</h3>

        <input
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />

        {/* ‚úÖ TYPE */}
        <select value={noticeType} onChange={(e) => setNoticeType(e.target.value)}>
          <option value="notice">‡§∏‡§æ‡§Æ‡§æ‡§®‡•ç‡§Ø ‡§∏‡•Ç‡§ö‡§®‡§æ</option>
          <option value="list">‡§ó‡§æ‡§µ‡§æ‡§ö‡•Ä ‡§Ø‡§æ‡§¶‡•Ä / ‡§¶‡§∏‡•ç‡§§‡§ê‡§µ‡§ú</option>
        </select>

        <label>
          <input
            type="checkbox"
            checked={isImportant}
            onChange={(e) => setIsImportant(e.target.checked)}
          />
          Important
        </label>

        {/* FILE ONLY FOR LIST */}
        {noticeType === "list" && (
          <input
            type="file"
            accept=".pdf,.jpg,.png"
            onChange={(e) => setFile(e.target.files[0])}
            required
          />
        )}

        <button disabled={isAdding}>
          {isAdding ? "Adding..." : "Add"}
        </button>
      </form>

      {/* LIST */}
      <div className="notice-list">
        {notices.map((n) => (
          <div key={n._id} className="notice-card">
            {n.noticeType === "list" && <span className="badge">üìÑ List</span>}
            {n.isImportant && <span className="badge important">‚ö†Ô∏è</span>}

            <h4>{n.title}</h4>
            <p>{n.description}</p>

            {n.attachment && (
              <a href={getAttachmentUrl(n.attachment)} target="_blank" rel="noreferrer">
                üìé View / Download
              </a>
            )}

            <div className="actions">
              <button onClick={() => openEditPopup(n)}>Edit</button>
              <button onClick={() => deleteNotice(n._id)}>
                {isDeleting === n._id ? "Deleting..." : "Delete"}
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* EDIT MODAL */}
      {editPopup && (
        <div className="modal">
          <input value={editTitle} onChange={(e) => setEditTitle(e.target.value)} />
          <textarea
            value={editDescription}
            onChange={(e) => setEditDescription(e.target.value)}
          />
          <input type="file" onChange={(e) => setEditFile(e.target.files[0])} />
          <button onClick={updateNotice}>
            {isUpdating ? "Saving..." : "Save"}
          </button>
        </div>
      )}
    </div>
  );
};

export default ManageNotices;
