import React, { useEffect, useState } from "react";
import "./ManageAbout.css";

const API = "https://backend-9i6n.onrender.com";

/* ‚úÖ EMPTY STRUCTURES */
const emptyWard = {
  name: "",
  totalVoters: "",
  maleVoters: "",
  femaleVoters: "",
  houses: "",
};

const emptySchool = {
  name: "",
  type: "",
};

const ManageAbout = () => {
  const token = localStorage.getItem("token");

  const [editMode, setEditMode] = useState(false);

  const [form, setForm] = useState({
    name: "",
    history: "",
    populationTotal: "",
    populationMale: "",
    populationFemale: "",
    literacyRate: "",
    area: "",
    description: "",
    hasBusService: false,
    schools: [{ ...emptySchool }, { ...emptySchool }],
    wards: [
      { ...emptyWard },
      { ...emptyWard },
      { ...emptyWard },
      { ...emptyWard },
    ],
  });

  /* ================= FETCH ================= */
  useEffect(() => {
    fetch(`${API}/api/village`)
      .then((res) => res.json())
      .then((data) => {
        if (!data) return;

        setForm({
          ...form,
          ...data,
          wards: Array.isArray(data.wards) && data.wards.length > 0
            ? data.wards
            : [
                { ...emptyWard },
                { ...emptyWard },
                { ...emptyWard },
                { ...emptyWard },
              ],
          schools: Array.isArray(data.schools) && data.schools.length > 0
            ? data.schools
            : [{ ...emptySchool }, { ...emptySchool }],
        });
      })
      .catch((err) => console.error("Village fetch error:", err));
      // eslint-disable-next-line
  }, []);

  /* ================= HANDLERS ================= */
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({ ...form, [name]: type === "checkbox" ? checked : value });
  };

  const handleWardChange = (index, field, value) => {
    const updated = [...form.wards];
    updated[index][field] = value;
    setForm({ ...form, wards: updated });
  };

  const handleSchoolChange = (index, field, value) => {
    const updated = [...form.schools];
    updated[index][field] = value;
    setForm({ ...form, schools: updated });
  };

  /* ================= SAVE ================= */
  const saveData = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch(`${API}/api/village`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(form),
      });

      if (!res.ok) throw new Error();

      alert("‚úÖ ‡§ó‡§æ‡§µ‡§æ‡§ö‡•Ä ‡§Æ‡§æ‡§π‡§ø‡§§‡•Ä ‡§Ø‡§∂‡§∏‡•ç‡§µ‡•Ä‡§∞‡•Ä‡§§‡•ç‡§Ø‡§æ ‡§ú‡§§‡§® ‡§ù‡§æ‡§≤‡•Ä");
      setEditMode(false);
    } catch {
      alert("‚ùå ‡§Æ‡§æ‡§π‡§ø‡§§‡•Ä ‡§ú‡§§‡§® ‡§ï‡§∞‡§§‡§æ ‡§Ü‡§≤‡•Ä ‡§®‡§æ‡§π‡•Ä");
    }
  };

  return (
    <div className="manage-about">
      <h2>üè° ‡§ó‡§æ‡§µ‡§æ‡§ö‡•Ä ‡§Æ‡§æ‡§π‡§ø‡§§‡•Ä (Admin)</h2>

      {/* ================= VIEW MODE ================= */}
      {!editMode && (
        <div className="about-card">
          <h3>{form.name}</h3>
          <p>{form.history}</p>

          <h4>üìä ‡§≤‡•ã‡§ï‡§∏‡§Ç‡§ñ‡•ç‡§Ø‡§æ</h4>
          <p>‡§è‡§ï‡•Ç‡§£: {form.populationTotal}</p>
          <p>‡§™‡•Å‡§∞‡•Å‡§∑: {form.populationMale}</p>
          <p>‡§∏‡•ç‡§§‡•ç‡§∞‡§ø‡§Ø‡§æ: {form.populationFemale}</p>
          <p>‡§∏‡§æ‡§ï‡•ç‡§∑‡§∞‡§§‡§æ ‡§¶‡§∞: {form.literacyRate}%</p>

          <h4>üó∫ ‡§ï‡•ç‡§∑‡•á‡§§‡•ç‡§∞‡§´‡§≥</h4>
          <p>{form.area} ‡§ö‡•å.‡§ï‡§ø.‡§Æ‡•Ä.</p>

          <h4>üöå ‡§∏‡•Å‡§µ‡§ø‡§ß‡§æ</h4>
          <p>‡§¨‡§∏ ‡§∏‡•á‡§µ‡§æ: {form.hasBusService ? "‡§â‡§™‡§≤‡§¨‡•ç‡§ß" : "‡§®‡§æ‡§π‡•Ä"}</p>

          <h4>üè´ ‡§∂‡§æ‡§≥‡§æ</h4>
          {form.schools?.map((s, i) => (
            <p key={i}>‚Ä¢ {s.name} ({s.type})</p>
          ))}

