import React, { useState, useContext, useEffect } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import { useLocation } from "react-router-dom";
import "./ApplyService.css";

const API =
  process.env.REACT_APP_API_URL || "https://backend-9i6n.onrender.com";

// тЬЕ MAX FILE SIZE = 500 KB
const MAX_FILE_SIZE = 500 * 1024;

const ApplyService = () => {
  const { token } = useContext(AuthContext);
  const location = useLocation();

  const [formData, setFormData] = useState({
    serviceType: "",
    fullName: "",
    address: "",
    mobile: "",
    deceasedName: "",
    dateOfDeath: "",
  });

  const [files, setFiles] = useState({});
  const [loading, setLoading] = useState(false);

  const [toast, setToast] = useState({
    show: false,
    message: "",
    type: "",
  });

  /* ================= PREFILL SERVICE FROM URL ================= */
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const serviceFromUrl = params.get("service");

    if (serviceFromUrl) {
      setFormData((prev) => ({
        ...prev,
        serviceType: serviceFromUrl,
      }));
    }
  }, [location.search]);

  /* ================= TOAST ================= */
  const showToast = (message, type) => {
    setToast({ show: true, message, type });
    setTimeout(() => {
      setToast({ show: false, message: "", type: "" });
    }, 1000);
  };

  /* ================= INPUT ================= */
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  /* ================= FILE (MAX 500 KB) ================= */
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (file.size > MAX_FILE_SIZE) {
      showToast("рдлрд╛рдИрд▓рдЪрд╛ рдЖрдХрд╛рд░ 500 KB рдкреЗрдХреНрд╖рд╛ рдЬрд╛рд╕реНрдд рдирд╕рд╛рд╡рд╛", "error");
      e.target.value = "";
      return;
    }

    setFiles({ ...files, [e.target.name]: file });
  };

  /* ================= SUBMIT ================= */
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    const data = new FormData();
    Object.entries(formData).forEach(([k, v]) => data.append(k, v));
    Object.entries(files).forEach(([k, f]) => data.append(k, f));

    try {
      await axios.post(`${API}/api/services/apply`, data, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      showToast("рдЕрд░реНрдЬ рдпрд╢рд╕реНрд╡реАрд░реАрддреНрдпрд╛ рд╕рд╛рджрд░ рдЭрд╛рд▓рд╛ тЬЕ", "success");

      setFormData({
        serviceType: "",
        fullName: "",
        address: "",
        mobile: "",
        deceasedName: "",
        dateOfDeath: "",
      });
      setFiles({});
    } catch (err) {
      showToast("рдЕрд░реНрдЬ рд╕рд╛рджрд░ рд╣реЛрдК рд╢рдХрд▓рд╛ рдирд╛рд╣реА тЭМ", "error");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="apply-service-container">
      <h2>рд╕реЗрд╡реЗрд╕рд╛рдареА рдЕрд░реНрдЬ рдХрд░рд╛</h2>

      <p className="file-help">
        ЁЯУМ рд╕рд░реНрд╡ рдХрд╛рдЧрджрдкрддреНрд░рд╛рдВрдЪреА рдлрд╛рдИрд▓ рд╕рд╛рдИрдЬ <b>500 KB рдкреЗрдХреНрд╖рд╛ рдХрдореА</b> рдЕрд╕рд╛рд╡реА. <br />
        ЁЯУЙ рдлрд╛рдИрд▓ рдореЛрдареА рдЕрд╕рд▓реНрдпрд╛рд╕ рдпреЗрдереЗ compress рдХрд░рд╛:{" "}
        <a
          href="https://www.ilovepdf.com/compress_image"
          target="_blank"
          rel="noopener noreferrer"
        >
          https://www.ilovepdf.com
        </a>
      </p>

      {toast.show && (
        <div className={`toast ${toast.type}`}>
          {toast.type === "success" ? "тЬЕ" : "тЭМ"} {toast.message}
        </div>
      )}

      <form onSubmit={handleSubmit} encType="multipart/form-data">
        {/* SERVICE (NOT DISABLED) */}
        <label>рд╕реЗрд╡рд╛ рдирд┐рд╡рдбрд╛</label>
        <select
          name="serviceType"
          value={formData.serviceType}
          onChange={handleChange}
          required
        >
          <option value="">-- рд╕реЗрд╡рд╛ рдирд┐рд╡рдбрд╛ --</option>
          <option value="рдЬрдиреНрдо рдкреНрд░рдорд╛рдгрдкрддреНрд░">рдЬрдиреНрдо рдкреНрд░рдорд╛рдгрдкрддреНрд░</option>
          <option value="рдореГрддреНрдпреВ рдкреНрд░рдорд╛рдгрдкрддреНрд░">рдореГрддреНрдпреВ рдкреНрд░рдорд╛рдгрдкрддреНрд░</option>
          <option value="рдЙрддреНрдкрдиреНрди рдкреНрд░рдорд╛рдгрдкрддреНрд░">рдЙрддреНрдкрдиреНрди рдкреНрд░рдорд╛рдгрдкрддреНрд░</option>
          <option value="рд░рд╣рд┐рд╡рд╛рд╕реА рдкреНрд░рдорд╛рдгрдкрддреНрд░">рд░рд╣рд┐рд╡рд╛рд╕реА рдкреНрд░рдорд╛рдгрдкрддреНрд░</option>
          <option value="рд╡рд┐рд╡рд╛рд╣ рдкреНрд░рдорд╛рдгрдкрддреНрд░">рд╡рд┐рд╡рд╛рд╣ рдкреНрд░рдорд╛рдгрдкрддреНрд░</option>
        </select>

        {/* APPLICANT DETAILS */}
        <h4>рдЕрд░реНрдЬрджрд╛рд░рд╛рдЪреА рдорд╛рд╣рд┐рддреА</h4>
        <input
          name="fullName"
          placeholder="рдкреВрд░реНрдг рдирд╛рд╡"
          value={formData.fullName}
          onChange={handleChange}
          required
        />
        <input
          name="address"
          placeholder="рдкрддреНрддрд╛"
          value={formData.address}
          onChange={handleChange}
          required
        />
        <input
          name="mobile"
          placeholder="рдореЛрдмрд╛рдИрд▓ рдХреНрд░рдорд╛рдВрдХ"
          value={formData.mobile}
          onChange={handleChange}
          required
        />

        {/* BIRTH CERTIFICATE */}
        {formData.serviceType === "рдЬрдиреНрдо рдкреНрд░рдорд╛рдгрдкрддреНрд░" && (
          <>
            <h4>ЁЯУД рдЖрд╡рд╢реНрдпрдХ рдХрд╛рдЧрджрдкрддреНрд░реЗ (рдЬрдиреНрдо рдкреНрд░рдорд╛рдгрдкрддреНрд░)</h4>

            <label>ЁЯПе рд░реБрдЧреНрдгрд╛рд▓рдпрд╛рдЪреА рдЬрдиреНрдо рдиреЛрдВрдж</label>
            <input
              type="file"
              accept=".jpg,.jpeg,.png,.pdf"
              name="hospitalSlip"
              onChange={handleFileChange}
              required
            />

            <label>ЁЯЖФ рдкрд╛рд▓рдХрд╛рдВрдЪреЗ рдЖрдзрд╛рд░ рдХрд╛рд░реНрдб</label>
            <input
              type="file"
              accept=".jpg,.jpeg,.png,.pdf"
              name="parentsAadhaar"
              onChange={handleFileChange}
              required
            />

            <label>ЁЯПа рдкрддреНрддрд╛ рдкреБрд░рд╛рд╡рд╛</label>
            <input
              type="file"
              name="addressProof"
              onChange={handleFileChange}
              required
            />
          </>
        )}

        {/* DEATH CERTIFICATE */}
        {formData.serviceType === "рдореГрддреНрдпреВ рдкреНрд░рдорд╛рдгрдкрддреНрд░" && (
          <>
            <h4>ЁЯСд рдореГрдд рд╡реНрдпрдХреНрддреАрдЪреА рдорд╛рд╣рд┐рддреА</h4>

            <input
              name="deceasedName"
              placeholder="рдореГрдд рд╡реНрдпрдХреНрддреАрдЪреЗ рдирд╛рд╡"
              value={formData.deceasedName}
              onChange={handleChange}
              required
            />

            <label>ЁЯУЕ рдореГрддреНрдпреВрдЪреА рддрд╛рд░реАрдЦ</label>
            <input
              type="date"
              name="dateOfDeath"
              value={formData.dateOfDeath}
              onChange={handleChange}
              required
            />

            <h4>ЁЯУД рдЖрд╡рд╢реНрдпрдХ рдХрд╛рдЧрджрдкрддреНрд░реЗ</h4>

            <label>ЁЯПе рдореГрддреНрдпреВ рдкреНрд░рдорд╛рдгрдкрддреНрд░ / рд░реБрдЧреНрдгрд╛рд▓рдпрд╛рдЪреА рдкрд╛рд╡рддреА</label>
            <input
              type="file"
              name="deathSlip"
              onChange={handleFileChange}
              required
            />

            <label>ЁЯЖФ рдореГрдд рд╡реНрдпрдХреНрддреАрдЪреЗ рдЖрдзрд╛рд░ рдХрд╛рд░реНрдб</label>
            <input
              type="file"
              name="deceasedAadhaar"
              onChange={handleFileChange}
              required
            />

            <label>ЁЯЖФ рдЕрд░реНрдЬрджрд╛рд░рд╛рдЪреЗ рдЖрдзрд╛рд░ рдХрд╛рд░реНрдб</label>
            <input
              type="file"
              name="applicantAadhaar"
              onChange={handleFileChange}
              required
            />
          </>
        )}

        {/* INCOME CERTIFICATE */}
        {formData.serviceType === "рдЙрддреНрдкрдиреНрди рдкреНрд░рдорд╛рдгрдкрддреНрд░" && (
          <>
            <h4>ЁЯУД рдЖрд╡рд╢реНрдпрдХ рдХрд╛рдЧрджрдкрддреНрд░реЗ</h4>

            <label>ЁЯЖФ рдЖрдзрд╛рд░ рдХрд╛рд░реНрдб</label>
            <input
              type="file"
              name="aadhaar"
              onChange={handleFileChange}
              required
            />

            <label>ЁЯНЪ рд░реЗрд╢рди рдХрд╛рд░реНрдб</label>
            <input
              type="file"
              name="rationCard"
              onChange={handleFileChange}
              required
            />

            <label>ЁЯТ╝ рдЙрддреНрдкрдиреНрдирд╛рдЪрд╛ рдкреБрд░рд╛рд╡рд╛</label>
            <input
              type="file"
              name="incomeProof"
              onChange={handleFileChange}
              required
            />
          </>
        )}
        {/* ================= MARRIAGE CERTIFICATE ================= */}
        {formData.serviceType === "рд╡рд┐рд╡рд╛рд╣ рдкреНрд░рдорд╛рдгрдкрддреНрд░" && (
          <>
            <h4>ЁЯУД рдЖрд╡рд╢реНрдпрдХ рдХрд╛рдЧрджрдкрддреНрд░реЗ (рд╡рд┐рд╡рд╛рд╣ рдкреНрд░рдорд╛рдгрдкрддреНрд░)</h4>

            <label>ЁЯЖФ рд╡рд░рдЪреЗ-рдЖрдзрд╛рд░ рдХрд╛рд░реНрдб</label>
            <input
              type="file"
              name="brideGroomAadhaar"
              onChange={handleFileChange}
              required
            />
            <label>ЁЯЖФ рд╡рдзреВрдЪреЗ рдЖрдзрд╛рд░ рдХрд╛рд░реНрдб</label>
            <input
              type="file"
              name="brideGroomAadhaar"
              onChange={handleFileChange}
              required
            />

            <label>ЁЯУЕ рд╡рдпрд╛рдЪрд╛ рдкреБрд░рд╛рд╡рд╛ (рдЬрдиреНрдо рдкреНрд░рдорд╛рдгрдкрддреНрд░ / рд╢рд╛рд│реЗрдЪрд╛ рджрд╛рдЦрд▓рд╛)</label>
            <input
              type="file"
              name="ageProof"
              onChange={handleFileChange}
              required
            />

            <label>ЁЯПа рдкрддреНрддрд╛ рдкреБрд░рд╛рд╡рд╛</label>
            <input
              type="file"
              name="addressProof"
              onChange={handleFileChange}
              required
            />

            <label>ЁЯТН рд╡рд┐рд╡рд╛рд╣ рд╕рдорд╛рд░рдВрднрд╛рдЪреЗ рдЫрд╛рдпрд╛рдЪрд┐рддреНрд░</label>
            <input
              type="file"
              name="marriagePhoto"
              onChange={handleFileChange}
              required
            />

            <label>ЁЯУЬ рд▓рдЧреНрди рдкрддреНрд░рд┐рдХрд╛ / рд╡рд┐рд╡рд╛рд╣ рдиреЛрдВрдж рдкреБрд░рд╛рд╡рд╛</label>
            <input
              type="file"
              name="marriageInvitation"
              onChange={handleFileChange}
              required
            />

            <label>ЁЯСе рджреЛрди рд╕рд╛рдХреНрд╖реАрджрд╛рд░рд╛рдВрдЪреЗ рдЖрдзрд╛рд░ рдХрд╛рд░реНрдб</label>
            <input
              type="file"
              name="witnessAadhaar"
              onChange={handleFileChange}
              required
            />
          </>
        )}

        {/* RESIDENCE CERTIFICATE */}
