import React, { useState, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import "./Navbar.css";
import logo from "../../assets/egram-logo.png";

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);
  const [language, setLanguage] = useState("en");

const toggleLanguage = () => {
  const newLang = language === "en" ? "mr" : "en";
  setLanguage(newLang);
  window.changeLanguage(newLang);
};


  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-brand">
          <img src={logo} alt="Logo" className="brand-logo" />
        </Link>

        <div className="nav-desktop-menu">
          <Link to="/">Home</Link>
          <Link to="/about">About</Link>
          <Link to="/services">Services</Link>
          <Link to="/schemes">Schemes</Link>
          <Link to="/notices">Notices</Link>
          <Link to="/development">Development</Link>
          <Link to="/gallery">Gallery</Link>
          <Link to="/contact">Members</Link>

          {/* üåç LANGUAGE TOGGLE */}
          <button className="lang-btn" onClick={toggleLanguage}>
            üåç
          </button>

          {!user && <Link to="/login">Login</Link>}

          {user && (
            <button onClick={() => navigate("/profile")}>
              üë§ {user.fullName?.split(" ")[0]}
            </button>
          )}
        </div>

        <button
          className="nav-toggle-button"
          onClick={() => setMenuOpen(!menuOpen)}
        >
          ‚ò∞
        </button>
      </div>

      {/* MOBILE MENU */}
      {menuOpen && (
        <div className="nav-mobile-menu-wrapper">
          <Link to="/" onClick={() => setMenuOpen(false)}>Home</Link>
          <Link to="/about" onClick={() => setMenuOpen(false)}>About</Link>
          <Link to="/services" onClick={() => setMenuOpen(false)}>Services</Link>
          <Link to="/schemes" onClick={() => setMenuOpen(false)}>Schemes</Link>
          <Link to="/notices" onClick={() => setMenuOpen(false)}>Notices</Link>

          {/* üåç MOBILE TOGGLE */}
          <button className="lang-btn" onClick={toggleLanguage}>
            üåç Change Language
          </button>

          {user && <button onClick={logout}>Logout</button>}
        </div>
      )}
    </nav>
  );
};

export default Navbar;
