import React, { useState, useContext, useEffect } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import { useLocation } from "react-router-dom";
import "./ApplyService.css";

const API =
  process.env.REACT_APP_API_URL ||
  "https://backend-9i6n.onrender.com";

const MAX_FILE_SIZE = 5 * 1024 * 1024; // 5MB

const ApplyService = () => {
  const { token } = useContext(AuthContext);
  const location = useLocation();

  const query = new URLSearchParams(location.search);
  const selectedService = query.get("service");

  const [formData, setFormData] = useState({
    serviceType: "",
    fullName: "",
    address: "",
    mobile: "",
    deceasedName: "",
    dateOfDeath: "",
  });

  const [files, setFiles] = useState({});
  const [toast, setToast] = useState({
    show: false,
    message: "",
    type: "",
  });

  /* ================= PREFILL SERVICE ================= */
  useEffect(() => {
    if (selectedService) {
      setFormData((prev) => ({
        ...prev,
        serviceType: selectedService,
      }));
    }
  }, [selectedService]);

  /* ================= TOAST ================= */
  const showToast = (message, type) => {
    setToast({ show: true, message, type });
    setTimeout(
      () => setToast({ show: false, message: "", type: "" }),
      2000
    );
  };

  /* ================= INPUT ================= */
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  /* ================= FILE ================= */
  const handleFileChange = (e) => {
    const file = e.target.files[0];

    if (file && file.size > MAX_FILE_SIZE) {
      showToast("File size must be less than 2MB", "error");
      e.target.value = "";
      return;
    }

    setFiles({ ...files, [e.target.name]: file });
  };

  /* ================= SUBMIT ================= */
  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();

    Object.entries(formData).forEach(([key, value]) =>
      data.append(key, value)
    );

    Object.entries(files).forEach(([key, file]) =>
      data.append(key, file)
    );

    try {
      const res = await axios.post(
        `${API}/api/services/apply`,
        data,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      showToast(
        res.data.message ||
          "Application submitted successfully! ✅",
        "success"
      );

      setTimeout(() => {
        setFormData({
          serviceType: selectedService || "",
          fullName: "",
          address: "",
          mobile: "",
          deceasedName: "",
          dateOfDeath: "",
        });
        setFiles({});
      }, 1500);
    } catch (err) {
      console.error("Apply service error:", err);
      showToast(
        err.response?.data?.message ||
          "Submission failed! ❌",
        "error"
      );
    }
  };

  return (
    <div className="apply-service-container">
      <h2>Apply for Service</h2>

      {/* TOAST */}
      {toast.show && (
        <div className="toast-overlay show">
          <div className={`toast ${toast.type}`}>
            <span>{toast.type === "success" ? "✅" : "❌"}</span>
            <p>{toast.message}</p>
          </div>
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <label>Service</label>
        <select
          name="serviceType"
          value={formData.serviceType}
          onChange={handleChange}
          disabled={!!selectedService}
          required
        >
          <option value="">Select Service</option>
          <option value="Birth Certificate">Birth Certificate</option>
          <option value="Death Certificate">Death Certificate</option>
          <option value="Income Certificate">Income Certificate</option>
          <option value="Residence Certificate">
            Residence Certificate
          </option>
        </select>

        <h4>Applicant Details</h4>
        <input
          name="fullName"
          placeholder="Full Name"
          value={formData.fullName}
          onChange={handleChange}
          required
        />
        <input
          name="address"
          placeholder="Address"
          value={formData.address}
          onChange={handleChange}
          required
        />
        <input
          name="mobile"
          placeholder="Mobile Number"
          value={formData.mobile}
          onChange={handleChange}
          required
        />

        {/* CONDITIONAL DOCUMENTS — NO CHANGE */}
        {/* Your existing document sections stay EXACTLY the same */}

        <button type="submit">Submit Application</button>
      </form>
    </div>
  );
};

export default ApplyService;
