import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import "./Login.css";

// ‚úÖ SAFE API URL (production + local)
const API = "https://backend-9i6n.onrender.com";

const Auth = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [isLoading, setIsLoading] = useState(false);

  // COMMON
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // REGISTER ONLY
  const [fullName, setFullName] = useState("");
  const [mobile, setMobile] = useState("");
  const [village, setVillage] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!isLogin && password !== confirmPassword) {
      alert("‡§™‡§æ‡§∏‡§µ‡§∞‡•ç‡§° ‡§ú‡•Å‡§≥‡§§ ‡§®‡§æ‡§π‡•Ä‡§§!");
      return;
    }

    setIsLoading(true);

    try {
      if (isLogin) {
        // üîê LOGIN
        const res = await axios.post(`${API}/api/auth/login`, {
          email,
          password,
        });

        login(res.data.token, res.data.user);

        if (res.data.user.role === "admin") {
          navigate("/admin/dashboard");
        } else {
          navigate("/profile");
        }
      } else {
        // üìù REGISTER
        await axios.post(`${API}/api/auth/register`, {
          fullName,
          email,
          password,
          mobile,
          village,
        });

        alert("‡§®‡•ã‡§Ç‡§¶‡§£‡•Ä ‡§Ø‡§∂‡§∏‡•ç‡§µ‡•Ä ‡§ù‡§æ‡§≤‡•Ä! ‡§ï‡•É‡§™‡§Ø‡§æ ‡§≤‡•â‡§ó‡§ø‡§® ‡§ï‡§∞‡§æ.");

        setIsLogin(true);
        setFullName("");
        setEmail("");
        setPassword("");
        setConfirmPassword("");
        setMobile("");
        setVillage("");
      }
    } catch (err) {
      alert(
        err.response?.data?.message ||
          `${isLogin ? "‡§≤‡•â‡§ó‡§ø‡§®" : "‡§®‡•ã‡§Ç‡§¶‡§£‡•Ä"} ‡§Ö‡§Ø‡§∂‡§∏‡•ç‡§µ‡•Ä ‡§ù‡§æ‡§≤‡•Ä`
      );
    } finally {
      setIsLoading(false);
    }
  };

  const toggleMode = () => {
    setIsLogin(!isLogin);
    setFullName("");
    setEmail("");
    setPassword("");
    setConfirmPassword("");
    setMobile("");
    setVillage("");
  };

  return (
    <div className="auth-container">
      <div className="auth-box">
        <div className="auth-header">
          <h1>{isLogin ? "‡§™‡•Å‡§®‡•ç‡§π‡§æ ‡§∏‡•ç‡§µ‡§æ‡§ó‡§§ ‡§Ü‡§π‡•á" : "‡§®‡§µ‡•Ä‡§® ‡§ñ‡§æ‡§§‡•á ‡§§‡§Ø‡§æ‡§∞ ‡§ï‡§∞‡§æ"}</h1>
          <p>
            {isLogin
              ? "‡§ï‡•É‡§™‡§Ø‡§æ ‡§™‡•Å‡§¢‡•á ‡§ú‡§æ‡§£‡•ç‡§Ø‡§æ‡§∏‡§æ‡§†‡•Ä ‡§≤‡•â‡§ó‡§ø‡§® ‡§ï‡§∞‡§æ"
              : "‡§∏‡•Å‡§∞‡•Ç ‡§ï‡§∞‡§£‡•ç‡§Ø‡§æ‡§∏‡§æ‡§†‡•Ä ‡§®‡•ã‡§Ç‡§¶‡§£‡•Ä ‡§ï‡§∞‡§æ"}
          </p>
        </div>

        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <div className="input-group">
              <label>‡§™‡•Ç‡§∞‡•ç‡§£ ‡§®‡§æ‡§µ</label>
              <input
                type="text"
                value={fullName}
                onChange={(e) => setFullName(e.target.value)}
                required
                disabled={isLoading}
              />
            </div>
          )}

          <div className="input-group">
            <label>‡§à-‡§Æ‡•á‡§≤</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              disabled={isLoading}
            />
          </div>

          {!isLogin && (
            <>
              <div className="input-group">
                <label>‡§Æ‡•ã‡§¨‡§æ‡§à‡§≤ ‡§ï‡•ç‡§∞‡§Æ‡§æ‡§Ç‡§ï</label>
                <input
                  type="text"
                  value={mobile}
                  onChange={(e) => setMobile(e.target.value)}
                  required
                  disabled={isLoading}
                />
              </div>

              <div className="input-group">
                <label>‡§ó‡§æ‡§µ‡§æ‡§ö‡•á ‡§®‡§æ‡§µ</label>
                <input
                  type="text"
                  value={village}
                  onChange={(e) => setVillage(e.target.value)}
                  required
                  disabled={isLoading}
                />
              </div>
            </>
          )}

          <div className="input-group">
            <label>‡§™‡§æ‡§∏‡§µ‡§∞‡•ç‡§°</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              disabled={isLoading}
            />
          </div>

          {!isLogin && (
            <div className="input-group">
              <label>‡§™‡§æ‡§∏‡§µ‡§∞‡•ç‡§°‡§ö‡•Ä ‡§™‡•Å‡§∑‡•ç‡§ü‡•Ä ‡§ï‡§∞‡§æ</label>
              <input
                type="password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
                disabled={isLoading}
              />
            </div>
          )}

          <button type="submit" className="submit-btn" disabled={isLoading}>
            {isLoading ? (
              <>
                <span className="loader"></span>
                <span className="loader-text">
                  {isLogin ? "‡§≤‡•â‡§ó‡§ø‡§® ‡§π‡•ã‡§§ ‡§Ü‡§π‡•á..." : "‡§®‡•ã‡§Ç‡§¶‡§£‡•Ä ‡§π‡•ã‡§§ ‡§Ü‡§π‡•á..."}
                </span>
              </>
            ) : (
              <span>{isLogin ? "‡§≤‡•â‡§ó‡§ø‡§® ‡§ï‡§∞‡§æ" : "‡§®‡•ã‡§Ç‡§¶‡§£‡•Ä ‡§ï‡§∞‡§æ"}</span>
            )}
          </button>
        </form>

        <div className="auth-footer">
          <p>
            {isLogin ? "‡§ñ‡§æ‡§§‡•á ‡§®‡§æ‡§π‡•Ä ‡§Ü‡§π‡•á?" : "‡§Ü‡§ß‡•Ä‡§ö ‡§ñ‡§æ‡§§‡•á ‡§Ü‡§π‡•á?"}
            <span className="toggle-link" onClick={toggleMode}>
              {isLogin ? " ‡§®‡•ã‡§Ç‡§¶‡§£‡•Ä ‡§ï‡§∞‡§æ" : " ‡§≤‡•â‡§ó‡§ø‡§® ‡§ï‡§∞‡§æ"}
            </span>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Auth;
