import React, { useEffect, useState } from "react";
import "./About.css";

const About = () => {
  const [village, setVillage] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/village")
      .then((res) => res.json())
      .then((data) => setVillage(data));
  }, []);

  if (!village) return <p className="loading">Loading village information...</p>;

  return (
    <div className="about-page">
      {/* HEADER */}
      <div className="about-header">
        <h1>{village.name}</h1>
        <p className="slogan">
          ‚ÄúClean Village ‚Ä¢ Strong Community ‚Ä¢ Bright Future‚Äù
        </p>
      </div>

      {/* HISTORY */}
      <div className="about-section">
        <h2>üè° Village History</h2>
        <p className="history">{village.history}</p>
      </div>

      {/* STATISTICS */}
      <div className="about-section">
        <h2>üìä Village Statistics</h2>
        <div className="stats">
          <div className="stat-card">
            <span>üë•</span>
            <h3>{village.populationTotal}</h3>
            <p>Total Population</p>
          </div>

          <div className="stat-card">
            <span>üë®</span>
            <h3>{village.populationMale}</h3>
            <p>Male</p>
          </div>

          <div className="stat-card">
            <span>üë©</span>
            <h3>{village.populationFemale}</h3>
            <p>Female</p>
          </div>

          <div className="stat-card">
            <span>üìê</span>
            <h3>{village.area}</h3>
            <p>Area (sq km)</p>
          </div>
        </div>
      </div>

      {/* DESCRIPTION */}
      {village.description && (
        <div className="about-section">
          <h2>üå± About Our Village</h2>
          <p className="desc">{village.description}</p>
        </div>
      )}

      {/* STATIC VISION SECTION */}
      <div className="about-section vision">
        <h2>üéØ Our Vision</h2>
        <ul>
          <li>‚úî Transparent and digital governance</li>
          <li>‚úî Clean and green village environment</li>
          <li>‚úî Quality education and healthcare</li>
          <li>‚úî Strong rural development and employment</li>
        </ul>
      </div>
    </div>
  );
};

export default About;
