import React, { useEffect, useState } from "react";
import "./HeroSection.css";

const API = "https://backend-9i6n.onrender.com";

const HeroSection = () => {
  const [leaders, setLeaders] = useState([]);

  useEffect(() => {
    const fetchLeaders = async () => {
      try {
        const res = await fetch(`${API}/api/contacts`);
        const data = await res.json();

        // âœ… filter only Sarpanch & Upa-Sarpanch
        const filtered = data.filter(
          (m) =>
            m.role === "Sarpanch" ||
            m.role === "upasarpanch"
        );

        setLeaders(filtered.slice(0, 2)); // safety: only 2
      } catch (err) {
        console.error("Failed to fetch leaders", err);
      }
    };

    fetchLeaders();
  }, []);

  return (
    <div className="hero-container">
      <img
        src="https://images.unsplash.com/photo-1586773860418-d37222d8fce3"
        alt="Village"
        className="hero-image"
      />

      {/* TEXT OVERLAY */}
      <div className="hero-overlay">
        <h1>Digital Gram Panchayat</h1>
        <h2>Palshi</h2>
        <p>
          Welcome to the official portal of Palshi Gram Panchayat.
          Serving citizens with transparency and technology.
        </p>
      </div>

      {/* ðŸ‘¥ SARPANCH & UPA-SARPANCH */}
      <div className="hero-members">
        {leaders.map((member) => (
          <div className="member-card" key={member._id}>
            <img
              src={member.photo}
              alt={member.name}
              className="member-photo"
              onError={(e) => {
                e.target.src =
                  "https://cdn-icons-png.flaticon.com/512/149/149071.png";
              }}
            />
            <h4>{member.name}</h4>
            <span>{member.role}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HeroSection;
