import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import "./UserProfile.css";

const UserProfile = () => {
  const { token, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);

  const fetchProfile = async () => {
    try {
      setLoading(true);
      const res = await axios.get("http://localhost:5000/api/user/profile", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setProfile(res.data);
    } catch (err) {
      console.error("Failed to load profile", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProfile();
  }, [token]);

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  const handleReapply = (serviceType) => {
    navigate(`/apply-service?service=${encodeURIComponent(serviceType)}`);
  };

  const handleCancel = async (id) => {
    if (!window.confirm("Are you sure you want to cancel this application?")) {
      return;
    }

    try {
      await axios.delete(`http://localhost:5000/api/services/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProfile();
    } catch (err) {
      alert("Failed to cancel application. Please try again.");
    }
  };

  if (loading) {
    return (
      <div className="profile-page">
        <div className="loading">Loading...</div>
      </div>
    );
  }

  if (!profile) return null;

  const { user, services } = profile;

  return (
    <div className="profile-page">
      <div className="profile-container">
        {/* Header */}
        <div className="profile-header">
          <div>
            <h1>User Profile</h1>
            <p>Welcome, {user.fullName}</p>
          </div>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>

        {/* User Information */}
        <div className="info-section">
          <h2>Personal Information</h2>
          <div className="info-grid">
            <div className="info-item">
              <label>Full Name</label>
              <p>{user.fullName}</p>
            </div>
            <div className="info-item">
              <label>Email</label>
              <p>{user.email}</p>
            </div>
            <div className="info-item">
              <label>Village</label>
              <p>{user.village}</p>
            </div>
            <div className="info-item">
              <label>Mobile</label>
              <p>{user.mobile}</p>
            </div>
          </div>
        </div>

        {/* Applications */}
        <div className="applications-section">
          <div className="section-header">
            <h2>Your Applications</h2>
            <button 
              className="new-application-btn"
              onClick={() => navigate('/apply-service')}
            >
              + New Application
            </button>
          </div>

          {services.length === 0 ? (
            <div className="empty-message">
              <p>No applications yet</p>
              <button 
                className="apply-btn"
                onClick={() => navigate('/apply-service')}
              >
                Apply for Service
              </button>
            </div>
          ) : (
            <div className="applications-list">
              {services.map((s) => (
                <div key={s._id} className={`application-card ${s.status.toLowerCase()}`}>
                  <div className="card-header">
                    <div>
                      <h3>{s.serviceType}</h3>
                      <span className={`status-tag ${s.status.toLowerCase()}`}>
                        {s.status}
                      </span>
                    </div>
                    <p className="date">
                      {new Date(s.createdAt).toLocaleDateString("en-IN", {
                        day: "numeric",
                        month: "short",
                        year: "numeric",
                      })}
                    </p>
                  </div>

                  {s.status === "Rejected" && s.adminRemark && (
                    <div className="remark">
                      <strong>Remark:</strong> {s.adminRemark}
                    </div>
                  )}

                  <div className="card-actions">
                    {s.status === "Rejected" && (
                      <button
                        className="btn-reapply"
                        onClick={() => handleReapply(s.serviceType)}
                      >
                        Re-Apply
                      </button>
                    )}

                    {s.status === "Pending" && (
                      <button
                        className="btn-cancel"
                        onClick={() => handleCancel(s._id)}
                      >
                        Cancel
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
