import React, { useEffect, useState } from "react";
import "./ManageNotices.css";

const API =
  process.env.REACT_APP_API_URL ||
  "https://backend-9i6n.onrender.com";

const ManageNotices = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [isImportant, setIsImportant] = useState(false);
  const [file, setFile] = useState(null);

  const [notices, setNotices] = useState([]);

  const [editPopup, setEditPopup] = useState(false);
  const [currentEdit, setCurrentEdit] = useState(null);
  const [editTitle, setEditTitle] = useState("");
  const [editDescription, setEditDescription] = useState("");
  const [editFile, setEditFile] = useState(null);

  const [isAdding, setIsAdding] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [isDeleting, setIsDeleting] = useState(null);

  const [popup, setPopup] = useState({
    show: false,
    message: "",
    type: "",
  });

  const token = localStorage.getItem("token");

  /* ================= POPUP ================= */
  const showPopup = (message, type = "success") => {
    setPopup({ show: true, message, type });
    setTimeout(() => {
      setPopup({ show: false, message: "", type: "" });
    }, 3000);
  };

  /* ================= FETCH ================= */
  const fetchNotices = async () => {
    try {
      const res = await fetch(`${API}/api/notices`);
      const data = await res.json();
      setNotices(Array.isArray(data) ? data : []);
    } catch (err) {
      showPopup("Failed to fetch notices", "error");
    }
  };

  useEffect(() => {
    fetchNotices();
  }, []);

  /* ================= ADD ================= */
  const addNotice = async (e) => {
    e.preventDefault();
    setIsAdding(true);

    try {
      const formData = new FormData();
      formData.append("title", title);
      formData.append("description", description);
      formData.append("isImportant", isImportant);
      if (file) formData.append("attachment", file);

      const res = await fetch(`${API}/api/notices`, {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` },
        body: formData,
      });

      if (!res.ok) throw new Error();

      setTitle("");
      setDescription("");
      setIsImportant(false);
      setFile(null);
      document.querySelector('input[type="file"]').value = "";

      showPopup("üéâ Notice added successfully!");
      fetchNotices();
    } catch {
      showPopup("Failed to add notice", "error");
    } finally {
      setIsAdding(false);
    }
  };

  /* ================= DELETE ================= */
  const deleteNotice = async (id) => {
    if (!window.confirm("Are you sure you want to delete this notice?")) return;

    setIsDeleting(id);

    try {
      const res = await fetch(`${API}/api/notices/${id}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!res.ok) throw new Error();

      showPopup("üóëÔ∏è Notice deleted");
      fetchNotices();
    } catch {
      showPopup("Failed to delete notice", "error");
    } finally {
      setIsDeleting(null);
    }
  };

  /* ================= EDIT ================= */
  const openEditPopup = (notice) => {
    setCurrentEdit(notice);
    setEditTitle(notice.title);
    setEditDescription(notice.description);
    setEditFile(null);
    setEditPopup(true);
  };

  const updateNotice = async () => {
    setIsUpdating(true);

    try {
      const formData = new FormData();
      formData.append("title", editTitle);
      formData.append("description", editDescription);
      if (editFile) formData.append("attachment", editFile);

      const res = await fetch(`${API}/api/notices/${currentEdit._id}`, {
        method: "PUT",
        headers: { Authorization: `Bearer ${token}` },
        body: formData,
      });

      if (!res.ok) throw new Error();

      setEditPopup(false);
      showPopup("‚ú® Notice updated");
      fetchNotices();
    } catch {
      showPopup("Failed to update notice", "error");
    } finally {
      setIsUpdating(false);
    }
  };

  return (
    <div className="manage-notices">
      {/* POPUP */}
      {popup.show && (
        <div className={`popup ${popup.type}`}>
          {popup.type === "success" ? "‚úÖ" : "‚ùå"} {popup.message}
        </div>
      )}

      <h2>üìã Manage Notices</h2>

      {/* ADD FORM */}
      <form className="notice-form" onSubmit={addNotice}>
        <input
          placeholder="Notice title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        <textarea
          placeholder="Notice description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />

        <label className="checkbox">
          <input
            type="checkbox"
            checked={isImportant}
            onChange={(e) => setIsImportant(e.target.checked)}
          />
          Mark as Important
        </label>

        <input
          type="file"
          accept=".jpg,.jpeg,.png,.pdf"
          onChange={(e) => setFile(e.target.files[0])}
        />

        <button disabled={isAdding}>
          {isAdding ? <span className="loader"></span> : "Add Notice"}
        </button>
      </form>

      {/* NOTICE LIST */}
      <div className="notice-list">
        {notices.map((n) => (
          <div key={n._id} className={`notice-card ${n.isImportant ? "important" : ""}`}>
            {n.isImportant && <span className="badge">Important</span>}

            <h4>{n.title}</h4>
            <p>{n.description}</p>

            {n.attachment && (
              <a
                href={n.attachment}
                target="_blank"
                rel="noreferrer"
                className="attachment"
              >
                üìé View Attachment
              </a>
            )}

            <div className="actions">
              <button onClick={() => openEditPopup(n)}>‚úèÔ∏è Edit</button>
              <button onClick={() => deleteNotice(n._id)} disabled={isDeleting === n._id}>
                {isDeleting === n._id ? <span className="loader red"></span> : "üóë Delete"}
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* EDIT MODAL */}
      {editPopup && (
        <div className="modal">
          <div className="modal-box">
            <h3>Edit Notice</h3>

            <input
              value={editTitle}
              onChange={(e) => setEditTitle(e.target.value)}
            />
            <textarea
              value={editDescription}
              onChange={(e) => setEditDescription(e.target.value)}
            />

            {currentEdit?.attachment && (
              <a href={currentEdit.attachment} target="_blank" rel="noreferrer">
                üìé Current File
              </a>
            )}

            <input
              type="file"
              accept=".jpg,.jpeg,.png,.pdf"
              onChange={(e) => setEditFile(e.target.files[0])}
            />

            <div className="actions">
              <button onClick={updateNotice} disabled={isUpdating}>
                {isUpdating ? <span className="loader"></span> : "Save"}
              </button>
              <button onClick={() => setEditPopup(false)}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageNotices;
