import React, { useEffect, useState } from "react";
import "./ManageNotices.css";

const API =
  process.env.REACT_APP_API_URL ||
  "https://backend-9i6n.onrender.com";

const ManageNotices = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [isImportant, setIsImportant] = useState(false);
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);

  const [notices, setNotices] = useState([]);

  const [editPopup, setEditPopup] = useState(false);
  const [currentEdit, setCurrentEdit] = useState(null);
  const [editTitle, setEditTitle] = useState("");
  const [editDescription, setEditDescription] = useState("");
  const [editFile, setEditFile] = useState(null);
  const [editPreview, setEditPreview] = useState(null);

  const token = localStorage.getItem("token");

  /* ================= FETCH ================= */
  const fetchNotices = async () => {
    const res = await fetch(`${API}/api/notices`);
    const data = await res.json();
    setNotices(Array.isArray(data) ? data : []);
  };

  useEffect(() => {
    fetchNotices();
  }, []);

  /* ================= IMAGE PREVIEW ================= */
  const handleFileChange = (file, setFileFn, setPreviewFn) => {
    setFileFn(file);
    if (file && file.type.startsWith("image")) {
      setPreviewFn(URL.createObjectURL(file));
    } else {
      setPreviewFn(null);
    }
  };

  /* ================= ADD ================= */
  const addNotice = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("isImportant", isImportant);
    if (file) formData.append("attachment", file);

    await fetch(`${API}/api/notices`, {
      method: "POST",
      headers: { Authorization: `Bearer ${token}` },
      body: formData,
    });

    setTitle("");
    setDescription("");
    setIsImportant(false);
    setFile(null);
    setPreview(null);
    fetchNotices();
  };

  /* ================= DELETE ================= */
  const deleteNotice = async (id) => {
    if (!window.confirm("Delete this notice?")) return;

    await fetch(`${API}/api/notices/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });

    fetchNotices();
  };

  /* ================= EDIT ================= */
  const openEdit = (n) => {
    setCurrentEdit(n);
    setEditTitle(n.title);
    setEditDescription(n.description);
    setEditPopup(true);
  };

  const updateNotice = async () => {
    const formData = new FormData();
    formData.append("title", editTitle);
    formData.append("description", editDescription);
    if (editFile) formData.append("attachment", editFile);

    await fetch(`${API}/api/notices/${currentEdit._id}`, {
      method: "PUT",
      headers: { Authorization: `Bearer ${token}` },
      body: formData,
    });

    setEditPopup(false);
    setEditFile(null);
    setEditPreview(null);
    fetchNotices();
  };

  /* ================= URL ================= */
  const getUrl = (url) =>
    url?.startsWith("http") ? url : `${API}${url}`;

  return (
    <div className="manage-notices">
      <h2>ðŸ“‹ Manage Notices</h2>

      {/* ADD FORM */}
      <form className="notice-form" onSubmit={addNotice}>
        <input
          type="text"
          placeholder="Notice Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        <textarea
          placeholder="Notice Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />

        <label className="check">
          <input
            type="checkbox"
            checked={isImportant}
            onChange={(e) => setIsImportant(e.target.checked)}
          />
          Important
        </label>

        <input
          type="file"
          accept="image/*,.pdf"
          onChange={(e) =>
            handleFileChange(e.target.files[0], setFile, setPreview)
          }
        />

        {preview && (
          <img src={preview} alt="preview" className="preview-img" />
        )}

        <button>Add Notice</button>
      </form>

      {/* LIST */}
      <div className="notice-list">
        {notices.map((n) => (
          <div key={n._id} className="notice-card">
            {n.isImportant && <span className="badge">Important</span>}
            <h4>{n.title}</h4>
            <p>{n.description}</p>

            {n.attachment && (
              <a
                href={getUrl(n.attachment)}
                target="_blank"
                rel="noopener noreferrer"
                className="view-link"
              >
                ðŸ“Ž View Attachment
              </a>
            )}

            <div className="actions">
              <button onClick={() => openEdit(n)}>Edit</button>
              <button onClick={() => deleteNotice(n._id)}>Delete</button>
            </div>
          </div>
        ))}
      </div>

      {/* EDIT MODAL */}
      {editPopup && (
        <div className="modal">
          <div className="modal-box">
            <h3>Edit Notice</h3>

            <input
              value={editTitle}
              onChange={(e) => setEditTitle(e.target.value)}
            />

            <textarea
              value={editDescription}
              onChange={(e) => setEditDescription(e.target.value)}
            />

            {currentEdit?.attachment && (
              <a
                href={getUrl(currentEdit.attachment)}
                target="_blank"
                rel="noopener noreferrer"
              >
                View Current File
              </a>
            )}

            <input
              type="file"
              accept="image/*,.pdf"
              onChange={(e) =>
                handleFileChange(
                  e.target.files[0],
                  setEditFile,
                  setEditPreview
                )
              }
            />

            {editPreview && (
              <img src={editPreview} alt="preview" className="preview-img" />
            )}

            <div className="actions">
              <button onClick={updateNotice}>Save</button>
              <button onClick={() => setEditPopup(false)}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageNotices;
