import React, { useEffect, useState } from "react";
import "./About.css";

const About = () => {
  const [village, setVillage] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/village")
      .then((res) => res.json())
      .then((data) => setVillage(data));
  }, []);

  if (!village) return <p>Loading...</p>;

  return (
    <div className="about-page">
      <h2>{village.name}</h2>

      <p className="history">{village.history}</p>

      <div className="stats">
        <div>üë• Total Population: <b>{village.populationTotal}</b></div>
        <div>üë® Male: <b>{village.populationMale}</b></div>
        <div>üë© Female: <b>{village.populationFemale}</b></div>
        <div>üìê Area: <b>{village.area}</b></div>
      </div>

      {village.description && (
        <p className="desc">{village.description}</p>
      )}
    </div>
  );
};

export default About;
