import React, { useState, useContext } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import "./Navbar.css";

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const location = useLocation();

  const [menuOpen, setMenuOpen] = useState(false);
  const [profileOpen, setProfileOpen] = useState(false);

  const handleLogout = () => {
    logout();
    setProfileOpen(false);
    navigate("/login");
  };

  const isActive = (path) => {
    return location.pathname === path ? "active" : "";
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="navbar-content">
          {/* BRAND */}
          <div className="navbar-brand">
            <Link to="/">Gram Panchayat</Link>
          </div>

          {/* DESKTOP MENU */}
          <div className="nav-desktop-menu">
            <Link to="/" className={isActive("/")}>Home</Link>
            <Link to="/about" className={isActive("/about")}>About</Link>
            <Link to="/services" className={isActive("/services")}>Services</Link>
            <Link to="/schemes" className={isActive("/schemes")}>Schemes</Link>
            <Link to="/notices" className={isActive("/notices")}>Notices</Link>
            <Link to="/development" className={isActive("/development")}>Development</Link>
            <Link to="/gallery" className={isActive("/gallery")}>Gallery</Link>
            <Link to="/contact" className={isActive("/contact")}>Members</Link>

            {/* NOT LOGGED IN */}
            {!user && (
              <Link to="/login" className="login-button">
                Login
              </Link>
            )}

            {/* LOGGED IN */}
            {user && (
              <div className="profile-dropdown">
                <button
                  className="profile-button"
                  onClick={() => setProfileOpen(!profileOpen)}
                >
                  {user.name || "Profile"} ▼
                </button>

                {profileOpen && (
                  <div className="profile-menu">
                    <Link
                      to="/profile"
                      onClick={() => setProfileOpen(false)}
                    >
                      My Profile
                    </Link>

                    <button
                      onClick={handleLogout}
                      className="logout-button"
                    >
                      Logout
                    </button>
                  </div>
                )}
              </div>
            )}
          </div>

          {/* MOBILE MENU BUTTON */}
          <div className="nav-mobile-button">
            <button
              className="nav-toggle-button"
              onClick={() => setMenuOpen(!menuOpen)}
            >
              ☰
            </button>
          </div>
        </div>
      </div>

      {/* MOBILE MENU */}
      {menuOpen && (
        <div className="nav-mobile-menu-wrapper">
          <Link to="/" onClick={() => setMenuOpen(false)} className={isActive("/")}>Home</Link>
          <Link to="/about" onClick={() => setMenuOpen(false)} className={isActive("/about")}>About</Link>
          <Link to="/services" onClick={() => setMenuOpen(false)} className={isActive("/services")}>Services</Link>
          <Link to="/schemes" onClick={() => setMenuOpen(false)} className={isActive("/schemes")}>Schemes</Link>
          <Link to="/notices" onClick={() => setMenuOpen(false)} className={isActive("/notices")}>Notices</Link>
          <Link to="/development" onClick={() => setMenuOpen(false)} className={isActive("/development")}>Development</Link>
          <Link to="/gallery" onClick={() => setMenuOpen(false)} className={isActive("/gallery")}>Gallery</Link>
          <Link to="/contact" onClick={() => setMenuOpen(false)} className={isActive("/contact")}>Members</Link>

          {!user && (
            <Link to="/login" onClick={() => setMenuOpen(false)}>
              Login
            </Link>
          )}

          {user && (
            <>
              <Link to="/profile" onClick={() => setMenuOpen(false)} className={isActive("/profile")}>
                My Profile
              </Link>

              <button
                onClick={handleLogout}
                className="mobile-logout-button"
              >
                Logout
              </button>
            </>
          )}
        </div>
      )}
    </nav>
  );
};

export default Navbar;
