import React, { useEffect, useState } from "react";

const ManageContacts = () => {
  const [name, setName] = useState("");
  const [role, setRole] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [members, setMembers] = useState([]);

  const token = localStorage.getItem("token");

  const fetchMembers = async () => {
    const res = await fetch("http://localhost:5000/api/contacts");
    const data = await res.json();
    setMembers(data);
  };

  useEffect(() => {
    fetchMembers();
  }, []);

  const addMember = async (e) => {
    e.preventDefault();

    await fetch("http://localhost:5000/api/contacts", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ name, role, phone, email }),
    });

    setName("");
    setRole("");
    setPhone("");
    setEmail("");
    fetchMembers();
  };

  const deleteMember = async (id) => {
    await fetch(`http://localhost:5000/api/contacts/${id}`, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    fetchMembers();
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Manage Gram Panchayat Members</h2>

      <form onSubmit={addMember} style={{ marginBottom: "20px" }}>
        <input placeholder="Name" value={name} onChange={e => setName(e.target.value)} required />
        <input placeholder="Role" value={role} onChange={e => setRole(e.target.value)} required />
        <input placeholder="Phone" value={phone} onChange={e => setPhone(e.target.value)} required />
        <input placeholder="Email (optional)" value={email} onChange={e => setEmail(e.target.value)} />
        <button type="submit">Add Member</button>
      </form>

      <ul>
        {members.map(m => (
          <li key={m._id}>
            {m.name} â€“ {m.role}
            <button onClick={() => deleteMember(m._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageContacts;
