import React, { useEffect, useState } from "react";
import "./ManageNotices.css";

const ManageNotices = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [notices, setNotices] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editTitle, setEditTitle] = useState("");
  const [editDescription, setEditDescription] = useState("");
  const [message, setMessage] = useState("");
  const [viewNotice, setViewNotice] = useState(null);

  const token = localStorage.getItem("token");
  

  const fetchNotices = async () => {
    const res = await fetch("http://localhost:5000/api/notices");
    const data = await res.json();
    setNotices(data);
  };

  useEffect(() => {
    fetchNotices();
  }, []);

  // ADD
  const addNotice = async (e) => {
    e.preventDefault();

    await fetch("http://localhost:5000/api/notices", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ title, description }),
    });

    setTitle("");
    setDescription("");
    setMessage("âœ… Notice added successfully");
    fetchNotices();
  };

  // DELETE
  const deleteNotice = async (id) => {
    if (!window.confirm("Delete this notice?")) return;

    await fetch(`http://localhost:5000/api/notices/${id}`, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    setMessage("ðŸ—‘ Notice deleted");
    fetchNotices();
  };

  // START EDIT
  const startEdit = (notice) => {
    setEditingId(notice._id);
    setEditTitle(notice.title);
    setEditDescription(notice.description);
  };

  // UPDATE
  const updateNotice = async (id) => {
    await fetch(`http://localhost:5000/api/notices/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({
        title: editTitle,
        description: editDescription,
      }),
    });

    setEditingId(null);
    setMessage("âœï¸ Notice updated successfully");
    fetchNotices();
  };
  
