  return (
    <div className="manage-notices">
      <h2>Manage Notices</h2>
      {message && <p className="success-msg">{message}</p>}

      {/* ADD FORM */}
      <form className="notice-form" onSubmit={addNotice}>
        <input
          placeholder="Notice Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Notice Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <button type="submit">Add Notice</button>
      </form>

      {/* NOTICE CARDS */}
      <div className="notice-list">
        {notices.map((n) => (
          <div className="notice-card" key={n._id}>
            {editingId === n._id ? (
              <>
                <input
                  value={editTitle}
                  onChange={(e) => setEditTitle(e.target.value)}
                />
                <textarea
                  value={editDescription}
                  onChange={(e) => setEditDescription(e.target.value)}
                />
                <input
  type="file"
  accept=".pdf,.doc,.docx"
  onChange={(e) => setFile(e.target.files[0])}
/>

                <button onClick={() => updateNotice(n._id)}>Save</button>
                <button onClick={() => setEditingId(null)}>Cancel</button>
              </>
            ) : (
              <>
                <h4>{n.title}</h4>
                <p>{n.description}</p>
                <div className="card-actions">
                  <button className="view-btn" onClick={() => setViewNotice(n)}>
                    View
                  </button>
                  <button
                    className="delete-btn"
                    onClick={() => deleteNotice(n._id)}
                  >
                    Delete
                  </button>
                </div>
                {viewNotice && (
                  <div className="modal-overlay">
                    <div className="modal">
                      <h3>{viewNotice.title}</h3>

                      {editingId === viewNotice._id ? (
                        <>
                          <input
                            value={editTitle}
                            onChange={(e) => setEditTitle(e.target.value)}
                          />
                          <textarea
                            value={editDescription}
                            onChange={(e) => setEditDescription(e.target.value)}
                          />
                        </>
                      ) : (
                        <p>{viewNotice.description}</p>
                      )}

                      <div className="modal-actions">
                        {editingId === viewNotice._id ? (
                          <button
                            className="edit-btn"
                            onClick={() => {
                              updateNotice(viewNotice._id);
                              setViewNotice(null);
                            }}
                          >
                            Save
                          </button>
                        ) : (
                          <button
                            className="edit-btn"
                            onClick={() => {
                              startEdit(viewNotice);
                            }}
                          >
                            Edit
                          </button>
                        )}

                        <button
                          className="close-btn"
                          onClick={() => {
                            setViewNotice(null);
                            setEditingId(null);
                          }}
                        >
                          Close
                        </button>
                      </div>
                    </div>
                  </div>
                )}
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManageNotices;
