import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import "./UserProfile.css";

/* ‚úÖ BACKEND URL */
const API =
  process.env.REACT_APP_API_URL ||
  "https://backend-9i6n.onrender.com";

const UserProfile = () => {
  const { token, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);

  /* ================= FETCH PROFILE ================= */
  const fetchProfile = async () => {
    try {
      setLoading(true);

      const res = await axios.get(`${API}/api/user/profile`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setProfile(res.data);
    } catch (err) {
      console.error("Profile fetch error:", err);
      if (err.response?.status === 401) {
        logout();
        navigate("/login");
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (token) fetchProfile();
  }, [token]);

  /* ================= DELETE APPLICATION ================= */
  const deleteApplication = async (id) => {
    if (!window.confirm("‡§π‡§æ ‡§Ö‡§∞‡•ç‡§ú ‡§π‡§ü‡§µ‡§æ‡§Ø‡§ö‡§æ ‡§Ü‡§π‡•á ‡§ï‡§æ?")) return;

    try {
      await axios.delete(`${API}/api/services/application/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      // UI update
      setProfile((prev) => ({
        ...prev,
        services: prev.services.filter((s) => s._id !== id),
      }));
    } catch (err) {
      alert("‡§Ö‡§∞‡•ç‡§ú ‡§π‡§ü‡§µ‡§§‡§æ ‡§Ü‡§≤‡§æ ‡§®‡§æ‡§π‡•Ä ‚ùå");
    }
  };

  /* ================= RE-APPLY ================= */
  const reApply = (serviceType) => {
    navigate(
      `/apply-service?service=${encodeURIComponent(serviceType)}`
    );
  };

  /* ================= LOADING ================= */
  if (loading) {
    return <p className="loading-text">Loading profile...</p>;
  }

  if (!profile) return null;

  const { user, services } = profile;

  return (
    <div className="profile-container">
      <h2>üë§ User Profile</h2>

      {/* USER INFO */}
      <div className="profile-card">
        <p><b>Name:</b> {user.fullName}</p>
        <p><b>Email:</b> {user.email || "N/A"}</p>
        <p><b>Mobile:</b> {user.mobile || "N/A"}</p>
        <p><b>Village:</b> {user.village || "N/A"}</p>

        {user.createdAt && (
          <p>
            <b>Joined:</b>{" "}
            {new Date(user.createdAt).toLocaleDateString()}
          </p>
        )}

        <button
          className="logout-btn"
          onClick={() => {
            logout();
            navigate("/login");
          }}
        >
          Logout
        </button>
      </div>

      {/* USER APPLICATIONS */}
      <h3>üìÑ Your Applications</h3>

      {services.length === 0 ? (
        <p className="no-data">No applications submitted yet</p>
      ) : (
        <div className="applications-list">
          {services.map((s) => (
            <div key={s._id} className="application-card">
              <p><b>Service:</b> {s.serviceType}</p>

              <p>
                <b>Status:</b>{" "}
                <span className={`status ${s.status.toLowerCase()}`}>
                  {s.status}
                </span>
              </p>

              {s.createdAt && (
                <p>
                  <b>Applied On:</b>{" "}
                  {new Date(s.createdAt).toLocaleDateString()}
                </p>
              )}

              {/* REJECT REASON */}
              {s.status === "Rejected" && s.adminRemark && (
                <p className="reject-reason">
                  <b>Reason:</b> {s.adminRemark}
                </p>
              )}

              {/* ACTION BUTTONS (ONLY IF REJECTED) */}
              {s.status === "Rejected" && (
                <div className="app-actions">
                  <button
                    className="btn-reapply"
                    onClick={() => reApply(s.serviceType)}
                  >
                    üîÅ Re-Apply
                  </button>

                  <button
                    className="btn-delete"
                    onClick={() => deleteApplication(s._id)}
                  >
                    üóëÔ∏è Delete
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default UserProfile;
