import React, { useEffect, useState } from "react";
import "./Notices.css";

/* ‚úÖ DEPLOYMENT-SAFE BACKEND URL */
const API =
  process.env.REACT_APP_API_URL ||
  "https://backend-9i6n.onrender.com";

const Notices = () => {
  const [notices, setNotices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [selectedNotice, setSelectedNotice] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    const fetchNotices = async () => {
      try {
        const res = await fetch(`${API}/api/notices`);
        if (!res.ok) throw new Error("Failed to fetch notices");

        const data = await res.json();

        if (Array.isArray(data)) {
          // IMPORTANT first, latest first
          const sorted = data.sort((a, b) => {
            if (a.isImportant === b.isImportant) {
              return new Date(b.createdAt) - new Date(a.createdAt);
            }
            return b.isImportant - a.isImportant;
          });

          setNotices(sorted);
        } else {
          setNotices([]);
        }
      } catch (err) {
        console.error("Notice fetch error:", err);
        setError("Unable to load notices");
      } finally {
        setLoading(false);
      }
    };

    fetchNotices();
  }, []);

  // Open modal with selected notice
  const openModal = (notice) => {
    setSelectedNotice(notice);
    setIsModalOpen(true);
    document.body.style.overflow = 'hidden'; // Prevent background scroll
  };

  // Close modal
  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedNotice(null);
    document.body.style.overflow = 'auto'; // Re-enable scroll
  };

  // Close modal on escape key
  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape' && isModalOpen) {
        closeModal();
      }
    };

    document.addEventListener('keydown', handleEscape);
    return () => document.removeEventListener('keydown', handleEscape);
  }, [isModalOpen]);

  /* ================= LOADING ================= */
  if (loading) {
    return (
      <div className="notice-container">
        <div className="loading-wrapper">
          <div className="spinner"></div>
          <p className="loading-text">‡§∏‡•Ç‡§ö‡§®‡§æ ‡§≤‡•ã‡§° ‡§π‡•ã‡§§ ‡§Ü‡§π‡•á‡§§....</p>
        </div>
      </div>
    );
  }

  /* ================= ERROR ================= */
  if (error) {
    return (
      <div className="notice-container">
        <div className="error-box">
          <p className="error-text">‚ö†Ô∏è {error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="notice-container">
      <div className="notice-content">
        {/* HEADER */}
        <div className="notice-header">
          <h1 className="header-title"> ‡§®‡§µ‡•Ä‡§® ‡§∏‡•Ç‡§ö‡§®‡§æ </h1>
          <p className="header-subtitle">
            ‡§®‡§µ‡•Ä‡§® ‡§ò‡•ã‡§∑‡§£‡§æ‡§Ç‡§ö‡•Ä ‡§Æ‡§æ‡§π‡§ø‡§§‡•Ä ‡§Ø‡•á‡§•‡•á ‡§Æ‡§ø‡§≥‡§µ‡§æ
          </p>
        </div>

        {/* NOTICE LIST */}
        {notices.length === 0 ? (
          <div className="empty-state">
            <p className="empty-text">‡§ï‡•ã‡§£‡§§‡•ç‡§Ø‡§æ‡§π‡•Ä ‡§∏‡•Ç‡§ö‡§®‡§æ ‡§â‡§™‡§≤‡§¨‡•ç‡§ß ‡§®‡§æ‡§π‡•Ä‡§§</p>
          </div>
        ) : (
          <div className="notice-grid">
            {notices.map((notice) => (
              <div
                key={notice._id}
                className={`notice-card ${
                  notice.isImportant ? "notice-important" : ""
                }`}
              >
                {notice.isImportant && (
                  <div className="important-ribbon">
                    <span>‡§Æ‡§π‡§§‡•ç‡§µ‡§æ‡§ö‡•á</span>
                  </div>
                )}

                <div className="notice-body">
                  <h3 className="notice-title">{notice.title}</h3>

                  <p className="notice-date">
                    {new Date(notice.createdAt).toLocaleDateString('en-IN', {
                      year: 'numeric',
                      month: 'long',
                      day: 'numeric'
                    })}
                  </p>

                  <p className="notice-description">
                    {notice.description.length > 150
                      ? `${notice.description.substring(0, 150)}...`
                      : notice.description}
                  </p>

                  {/* View Details Button */}
                  <button
                    className="view-details-btn"
                    onClick={() => openModal(notice)}
                  >
                    <span>‡§∏‡§Ç‡§™‡•Ç‡§∞‡•ç‡§£ ‡§§‡§™‡§∂‡•Ä‡§≤ ‡§™‡§π‡§æ</span>
                    <svg
                      width="16"
                      height="16"
                      viewBox="0 0 16 16"
                      fill="none"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        d="M6 12L10 8L6 4"
                        stroke="currentColor"
                        strokeWidth="2"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                      />
                    </svg>
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* MODAL POPUP */}
      {isModalOpen && selectedNotice && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-container" onClick={(e) => e.stopPropagation()}>
            {/* Close Button */}
            <button className="modal-close-btn" onClick={closeModal}>
              <svg
                width="24"
                height="24"
                viewBox="0 0 24 24"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  d="M18 6L6 18M6 6L18 18"
                  stroke="currentColor"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                />
              </svg>
            </button>

            {/* Modal Header */}
            <div className="modal-header">
              {selectedNotice.isImportant && (
                <div className="modal-important-badge">
                  <span>‚≠ê ‡§Æ‡§π‡§§‡•ç‡§µ‡§æ‡§ö‡•á</span>
                </div>
              )}
              <h2 className="modal-title">{selectedNotice.title}</h2>
              <p className="modal-date">
                üìÖ {new Date(selectedNotice.createdAt).toLocaleDateString('en-IN', {
                  year: 'numeric',
                  month: 'long',
                  day: 'numeric',
                  weekday: 'long'
                })}
              </p>
            </div>

            {/* Modal Body */}
            <div className="modal-body">
              <p className="modal-description">{selectedNotice.description}</p>

              {/* Attachment */}
              {selectedNotice.attachment && (
                <div className="modal-attachment-section">
                  {selectedNotice.attachment.match(/\.(jpg|jpeg|png|webp)$/i) ? (
                    <div className="modal-image-wrapper">
                      <img
                        src={selectedNotice.attachment}
                        alt="Notice Attachment"
                        className="modal-image"
                        loading="lazy"
                      />
                    </div>
                  ) : (
                    <a
                      href={selectedNotice.attachment}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="modal-download-btn"
                    >
                      <svg
                        width="20"
                        height="20"
                        viewBox="0 0 20 20"
                        fill="none"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          d="M10 13L6 9M10 13L14 9M10 13V3M3 17H17"
                          stroke="currentColor"
                          strokeWidth="2"
                          strokeLinecap="round"
                          strokeLinejoin="round"
                        />
                      </svg>
                      <span>‡§°‡§æ‡§â‡§®‡§≤‡•ã‡§° / ‡§™‡§π‡§æ ‡§¶‡§∏‡•ç‡§§‡§ê‡§µ‡§ú</span>
                    </a>
                  )}
                </div>
              )}
            </div>

            {/* Modal Footer */}
            <div className="modal-footer">
              <button className="modal-close-footer-btn" onClick={closeModal}>
                ‡§¨‡§Ç‡§¶ ‡§ï‡§∞‡§æ
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Notices;
