import React, { useState, useContext, useEffect } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import { useLocation } from "react-router-dom";
import "./Service.css";

const MAX_FILE_SIZE = 2 * 1024 * 1024; // 2MB

const ApplyService = () => {
  const { token } = useContext(AuthContext);
  const location = useLocation();

  const query = new URLSearchParams(location.search);
  const selectedService = query.get("service");

  const [formData, setFormData] = useState({
    serviceType: "",
    fullName: "",
    address: "",
    mobile: "",
    deceasedName: "",
    dateOfDeath: ""
  });

  const [files, setFiles] = useState({});
  const [message, setMessage] = useState("");
  const [isError, setIsError] = useState(false);

  useEffect(() => {
    if (selectedService) {
      setFormData((prev) => ({ ...prev, serviceType: selectedService }));
    }
  }, [selectedService]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // ðŸ“ FILE HANDLER WITH SIZE CHECK
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && file.size > MAX_FILE_SIZE) {
      alert("File size must be less than 2MB");
      e.target.value = "";
      return;
    }
    setFiles({ ...files, [e.target.name]: file });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setIsError(false);

    const data = new FormData();

    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });

    Object.keys(files).forEach((key) => {
      data.append(key, files[key]);
    });

    try {
      const res = await axios.post(
        "http://localhost:5000/api/services/apply",
        data,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data"
          }
        }
      );

      setMessage(res.data.message || "Application submitted successfully");
      setIsError(false);
    } catch (err) {
      setMessage(err.response?.data?.message || "Submission failed");
      setIsError(true);
    }
  };

  return (
    <div className="apply-service-container">
      <h2>Apply for Service</h2>

      <form onSubmit={handleSubmit}>
        {/* SERVICE */}
        <label>Service</label>
        <select
          name="serviceType"
          value={formData.serviceType}
          onChange={handleChange}
          disabled={!!selectedService}
          required
        >
          <option value="">Select Service</option>
          <option value="Birth Certificate">Birth Certificate</option>
          <option value="Death Certificate">Death Certificate</option>
          <option value="Income Certificate">Income Certificate</option>
          <option value="Residence Certificate">Residence Certificate</option>
          <option value="Marriage Certificate">Marriage Certificate</option>
        </select>

        {/* APPLICANT */}
        <h4>Applicant Details</h4>
        <input name="fullName" placeholder="Full Name" onChange={handleChange} required />
        <input name="address" placeholder="Address" onChange={handleChange} required />
        <input name="mobile" placeholder="Mobile Number" onChange={handleChange} required />

        {/* ================= SERVICE-WISE DOCUMENTS ================= */}

        {/* MARRIAGE CERTIFICATE */}
        {formData.serviceType === "Marriage Certificate" && (
          <>
            <h4>Required Documents</h4>

            <input type="file" name="brideAadhaar" onChange={handleFileChange} required />
            <small>Bride Aadhaar (max 2MB)</small>

            <input type="file" name="groomAadhaar" onChange={handleFileChange} required />
            <small>Groom Aadhaar (max 2MB)</small>

            <input type="file" name="marriagePhoto" onChange={handleFileChange} required />
            <small>Marriage Photo (max 2MB)</small>

            <input type="file" name="addressProof" onChange={handleFileChange} required />
            <small>Address Proof (max 2MB)</small>
          </>
        )}

        <button type="submit">Submit Application</button>

        {/* âœ… SUCCESS / ERROR MESSAGE BELOW BUTTON */}
        {message && (
          <p className={isError ? "msg error" : "msg success"}>
            {message}
          </p>
        )}
      </form>
    </div>
  );
};

export default ApplyService;
